<?php /** * @package		AwoDev_AwoRewards * @copyright	Copyright (C) Seyi Awofadeju - All rights reserved. * @website		http://awodev.com * @license		http://awodev.com/content/magento-license **/class AwoDev_AwoRewards_Block_Adminhtml_Customer_Tab_Rewards extends Mage_Adminhtml_Block_Template implements Mage_Adminhtml_Block_Widget_Tab_Interface {	public function __construct() {		$this->setTemplate('pmainguet/aworewards/customer/tab/rewards.phtml');	}	public function getCustomtabInfo(){        $customer = Mage::registry('current_customer');        $customtab='My Custom tab Action Contents Here';		return $customtab;	}	public function getPointTotal() {		return Mage::helper('awodev_aworewards')->getCustomerPointTotal(Mage::registry('current_customer')->getId());	}	public function getLink() {		return Mage::helper('awodev_aworewards')->registration_link(Mage::registry('current_customer')->getId());	}		public function getTabLabel() { return $this->__('AwoRewards'); }	public function getTabTitle() { return $this->__('AwoRewads'); }	public function canShowTab() {		$customer = Mage::registry('current_customer');		return (bool)$customer->getId();	}	public function isHidden() { return false; }}